         fd.totalPhysicalAcceleration[1] = 0.0;
         fd.totalPhysicalAcceleration[2] = 0.0;
         fd.totalPhysicalDisplacement[0] = 0.0;
         fd.totalPhysicalDisplacement[1] = 0.0;
         fd.totalPhysicalDisplacement[2] = 0.0;
